///////////////////////////////////////////////////////////////////////
//download individual
///////////////////////////////////////////////////////////////////////
function convertioApiCall(index,type){
    console.log(index);
    var elem = document.getElementById(index);
    var svg = elem.outerHTML;
    
    var comboValue = $('#barcodeType').val();
    var bar_type = "EAN-8";
    switch(comboValue){
        case "EAN8" : 
            bar_type = "EAN-8"
            break;
        case "EAN13" : 
            bar_type = "EAN-13";
            break;
    }
    var value = $('#'+index).attr('value');

    var data = {
                    "apikey" : "beae8c11b61a55427c7780836217566c",
                    "input"  : "raw",
                    "file"   : svg,
                    "filename" : bar_type+"_"+value+".svg",
                    "outputformat" : type
                };
    data = JSON.stringify(data)
    console.log(data);
    $.ajax({
        type: 'POST',
        url: "https://api.convertio.co/convert",
        contentType: "application/json",
        data: data,
        success : function(e){
            console.log(e)
            if(e.code == 200){
                //get download url
                setTimeout(() => {
                    $.ajax({
                        type : 'GET',
                        url : "https://api.convertio.co/convert/"+e.data.id+"/status",
                        // data : JSON.stringify({
                        //     id : e.data.id
                        // }),
                        success : function(e){
                            console.log('download info ', e);
                            var url = e.data.output.url;
                            window.open(url);
                        }
                    })    
                }, 3000);
                
            }
        },
        error: function(e) {
          console.log(e);
        },
    });
}
