function downloadJPG(index){
    //jpg test
    var elem = document.getElementById(index);
    var content = elem.outerHTML;
    
    var comboValue = $('#barcodeType').val();
    var bar_type = "EAN-8";
    switch(comboValue){
        case "EAN8" : 
            bar_type = "EAN-8"
            break;
        case "EAN13" : 
            bar_type = "EAN-13";
            break;
    }
    var value = $('#'+index).attr('value');
    dialog.showSaveDialog(
        {
            filters : [
                { name: 'Image', extensions: ['jpg'] }
            ],
            defaultPath : bar_type+"_"+value+".jpg"
        },
        function(path){
            (async () => {
                await svgToImg.from(content).toJpeg({
                    path: path
                });
            })();
        }
    );
}

function downloadPDF(index){
    var elem = document.getElementById(index);
    var html = elem.outerHTML;
    var comboValue = $('#barcodeType').val();
    var bar_type = "EAN-8";
    switch(comboValue){
        case "EAN8" : 
            bar_type = "EAN-8"
            break;
        case "EAN13" : 
            bar_type = "EAN-13";
            break;
    }
    var value = $('#'+index).attr('value');
    var opt = {
        margin:       0,
        filename:     bar_type+"_"+value+".pdf",
        image:        { type: 'jpeg', quality: 0.98 },
        html2canvas:  { scale: 30 },
        jsPDF:        { orientation : 'l', unit: 'px', format: [$('#'+index).width(), $('#'+index).height()+10]}
      };
      
    // New Promise-based usage:
    html2pdf().set(opt).from(html).save();
}

function downloadAI(index){
    convertioApiCall(index,'ai');
}

function downloadEPS(index){
    convertioApiCall(index,'eps');
}

///////////////////////////////////////////////////////////////////////
//download bulk
///////////////////////////////////////////////////////////////////////
function savePngBulk(){
    var comboValue = $('#barcodeType').val();
    var bar_type = "EAN-8";
    switch(comboValue){
        case "EAN8" : 
            bar_type = "EAN-8"
            break;
        case "EAN13" : 
            bar_type = "EAN-13";
            break;
    }

    var pathList = [];
    console.log(tempDir);
    $('svg[id*=image-]').each(function() {
        var index = $(this).attr('id');
        var value = $(this).attr('value');
        console.log(index,value);
        var elem = document.getElementById(index);
        var html = elem.outerHTML;
        var path = tempDir+"/"+bar_type+"_"+value+".png";
        pathList.push(path);
        console.log(path);
        svgToImg.from(html).toJpeg({
            path: path
        });
    });
    console.log(pathList);

    setTimeout(() => {
        dialog.showSaveDialog(
            {
                filters : [
                    { name: 'achives', extensions: ['zip'] }
                ],
                defaultPath : bar_type+"_png_list.zip"
            },
            function(path){
                //achive
                var zip = new AdmZip();
                for(var i in pathList)
                    zip.addLocalFile(pathList[i]);
                zip.writeZip(path);
            }
        );
    }, pathList.length * 1500);
}
